import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { Switch } from "react-router-dom";
import "./App.css"; // Assuming you have a CSS file for styling

// Import data
import songData from "./songData";
import artistData from "./artistData";
import albumData from "./albumData";

function SongList() {
  return (
    <div className="song-list">
      {songData.map(song => (
        <div className="song" key={song.title}>
          <img src={song.image} alt={song.title} />
          <p>{song.title} - {song.artist}</p>
        </div>
      ))}
    </div>
  );
}

function ArtistList() {
  return (
    <div className="artist-list">
      {artistData.map(artist => (
        <div className="artist" key={artist.title}>
          <img src={artist.image} alt={artist.title} />
          <p>{artist.title}</p>
        </div>
      ))}
    </div>
  );
}

function AlbumList() {
  return (
    <div className="album-list">
      {albumData.map(album => (
        <div className="album" key={album.title}>
          <img src={album.image} alt={album.title} />
          <p>{album.title} - {album.artist}</p>
        </div>
      ))}
    </div>
  );
}

function App() {
  const [currentSong, setCurrentSong] = useState(null);

  // Function to play the selected song
  const playSong = song => {
    setCurrentSong(song);
  };

  return (
    <Router>
      <div className="App">
        <header>
          <h1>Muziek Streaming App</h1>
          <nav>
            <ul>
              <li>
                <Link to="/songs">Nummers</Link>
              </li>
              <li>
                <Link to="/albums">Albums</Link>
              </li>
              <li>
                <Link to="/artists">Artiesten</Link>
              </li>
            </ul>
          </nav>
        </header>
        <main>
          <Switch>
            <Route path="/songs" component={SongList} />
            <Route path="/albums" component={AlbumList} />
            <Route path="/artists" component={ArtistList} />
          </Switch>
        </main>
        {currentSong && (
          <div className="music-player">
            <h2>Now Playing</h2>
            <p>{currentSong.title} - {currentSong.artist}</p>
            <audio controls autoPlay>
              <source src={currentSong.mp3} type="audio/mpeg" />
              Your browser does not support the audio element.
            </audio>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
