import React, { useState } from "react";
import "./App.css"; // Assuming you have a CSS file for styling

// Import data
import songData from "./songData";
import artistData from "./artistData";
import albumData from "./albumData";

function App() {
  // State for search query
  const [searchQuery, setSearchQuery] = useState("");

  // Filtered song data based on search query
  const filteredSongs = songData.filter(song =>
    song.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    song.artist.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Function to handle search input change
  const handleSearchInputChange = event => {
    setSearchQuery(event.target.value);
  };

  return (
    <div className="App">
      <header>
        <h1>Muziek Streaming App</h1>
        <input
          type="text"
          placeholder="Zoeken naar nummers, albums of artiesten"
          value={searchQuery}
          onChange={handleSearchInputChange}
        />
      </header>
      <main>
        <section>
          <h2>Nummers</h2>
          <div className="song-list">
            {filteredSongs.map(song => (
              <div className="song" key={song.title}>
                <img src={song.image} alt={song.title} />
                <p>{song.title} - {song.artist}</p>
                {/* Add music player here */}
              </div>
            ))}
          </div>
        </section>
        <section>
          <h2>Artiesten</h2>
          <div className="artist-list">
            {artistData.map(artist => (
              <div className="artist" key={artist.title}>
                <img src={artist.image} alt={artist.title} />
                <p>{artist.title}</p>
              </div>
            ))}
          </div>
        </section>
        <section>
          <h2>Albums</h2>
          <div className="album-list">
            {albumData.map(album => (
              <div className="album" key={album.title}>
                <img src={album.image} alt={album.title} />
                <p>{album.title} - {album.artist}</p>
              </div>
            ))}
          </div>
        </section>
      </main>
      <footer>
        {/* Add footer content here */}
      </footer>
    </div>
  );
}

export default App;
