import React, { useState } from "react";
import Layout from "./Layout"; // Import Layout component
import "./App.css"; 

import songData from "./songData";

function App() {
  const [searchQuery, setSearchQuery] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);  
  const [currentSong, setCurrentSong] = useState(null); // Define currentSong and setCurrentSong

  const filteredSongs = songData.filter(
    (song) =>
      song.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      song.artist.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleSearchInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const playSong = (song) => {
    setCurrentSong(song);
    setIsPlaying(true);
  };

  return (
    <Layout>
      <div className="App">
        <header>
          <h1>Muziek Streaming App</h1>
          <input
            type="text"
            placeholder="Zoeken naar nummers, albums of artiesten"
            value={searchQuery}
            onChange={handleSearchInputChange}
          />
        </header>
        <main>
          <div className="mobile-container">
            <section>
              <h2>Nummers</h2>
              <div className="song-list">
                {filteredSongs.map((song) => (
                  <div
                    className={`song ${currentSong && currentSong.title === song.title && isPlaying ? 'active' : ''}`}
                    key={song.title}
                    onClick={() => playSong(song)}
                  >
                    <img src={song.image} alt={song.title} />
                    <p>{song.title}</p>
                  </div>
                ))}
              </div>
            </section>
          </div>
        </main>
      </div>
    </Layout>
  );
}

export default App;
